import classes from './AvailableMedicine.module.css';
import MedicineItem from './MedicineItem/MedicineItem';
import Card from '../UI/Card';

const DUMMY_MEDICINE = [
    {
      id: 'p1',
      name: 'paracetamol',
      description: 'pain reliever and a fever reducer.',
      price: 50.99,
    },
    {
      id: 'p2',
      name: 'combiflam',
      description: 'Reduce pain and inflamation',
      price: 16.5,
    },
    {
      id: 'p3',
      name: 'tylenol',
      description: 'Pain releiver and Fever reduction',
      price: 12.99,
    },
    {
      id: 'p4',
      name: 'zyrtec',
      description: 'Releive in itching, runny nose, watery eyes, and sneezing ',
      price: 18.99,
    },
  ];

  const AvailableMedicine =()=>{
      const medicineList = DUMMY_MEDICINE.map(medicine => (
      <MedicineItem 
        key={medicine.id} 
        name={medicine.name} 
        description={medicine.description}
        price={medicine.price}
        />
        ));

      return(
          <section className={classes.medicine}>
              <Card>
              <ul>{medicineList}</ul>
              </Card>
          </section>
      );
  }

  export default AvailableMedicine;
