import Input from '../../UI/Input';
import classes from './MedicineItemForm.module.css';
import { useRef, useState } from 'react';
const MedicineItemForm =(props) =>{
    const [quantityIsValid, setQuantityIsValid]=useState(false);
    const quantityInputRef = useRef();
    const submitHandler =(event) =>{
        event.prevent.Default();
        const enteredQuantity = quantityInputRef.current.value;
        const eneteredQuantityNumber = +enteredQuantity
        if (enteredQuantity.trim().length === 0 || eneteredQuantityNumber <1 || eneteredQuantityNumber > 4){
            setQuantityIsValid(false);
            return;
        };

        props.onAddToCart(eneteredQuantityNumber);
    }

    return(
        <form className={classes.form} onSubmit={submitHandler}>
            <Input 
            ref={quantityInputRef}
            label='Quantity' input={{
 //               id: 'amount_' + props.id,
                id: 'quantity_' + props.id,
                type: 'number',
                min:'1',
                max:'4',
                step:'1',
                defaultValue:'1'
            }}/>
            <button>
                +Add
            </button>
            {!quantityIsValid && <p>Please enter Valid Quantity</p>}
        </form>
    );
};

export default MedicineItemForm;
