import input from '../../UI/Input';
import classes from './MedicineItemForm.module.css';

const MedicineItemForm =(props) =>{

    return(
        <form className={classes.form}>
            <Input label="Amount" input={{
                id: 'amount',
                type: 'number',
                min:'1',
                max:'4',
                step:'1',
                defaultValue:'1'
            }}/>
            <button>
                +Add
            </button>
        </form>
    );
};

export default MedicineItemForm;
